import React, { useState } from 'react';

const UrlForm = ({ addUrl }) => {
  const [formData, setFormData] = useState({ name: '', url: '' });
  const [error, setError] = useState('');

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Check if the required fields are not empty
    if (!formData.name || !formData.url) {
      setError('Please enter both name and URL.');
      return;
    }

    // Clear any previous errors
    setError('');

    // Call the addUrl function to send the data to the backend
    addUrl(formData)
      .then(() => {
        // On successful addition, reset the form fields
        setFormData({ name: '', url: '' });
      })
      .catch((error) => {
        setError('Error adding URL. Please try again later.');
        console.error('Error adding URL:', error);
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      {error && <p>{error}</p>}
      <input type="text" name="name" value={formData.name} onChange={handleInputChange} placeholder="Name" />
      <input type="text" name="url" value={formData.url} onChange={handleInputChange} placeholder="URL" />
      <button type="submit">Add URL</button>
    </form>
  );
};

export default UrlForm;
